// URL of the page you want to scrape\n$url = 'http://example.com/your/page';\n\n// Initialize DOMDocument\n$doc = new DOMDocument();\n\n// Load the HTML content\n$doc->loadHTMLFile($url);\n\n// Find all the anchor tags\n$anchorTags = $doc->getElementsByTagName('a');\n\n// Open a file in write mode\n$file = fopen("urls.txt", "w");\n\n// Loop through all anchor tags\nforeach ($anchorTags as $tag) {\n    // Get the href attribute\n    $href = $tag->getAttribute('href');\n\n    // Make sure it's not empty to avoid writing blank lines\n    if (!empty($href)) {\n        // Write the URL to the file followed by a newline\n        fwrite($file, $href . "\n");\n    }\n}\n\n// Close the file\nfclose($file);\n\necho "URLs have been written to urls.txt";