use Zend_Date;\n\n$inputString = '02-14-2023'; // mm-dd-yyyy format\n$inputFormat = 'MM-DD-YYYY'; // specify the input format (Zend_Date format)\n$outputFormat = 'YYYYMMDD'; // desired output format (Zend_Date format)\n\n$date = new Zend_Date($inputString, $inputFormat);\nif ($date->isValid()) {\n    $mysqlDate = $date->toString($outputFormat);\n    echo $mysqlDate; // outputs: 20230214\n} else {\n    // handle invalid date string\n}