// Model/DbTable/User.php\nclass User extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'users';\n    // ...\n}\n\n// Model/DbTable/Order.php\nclass Order extends Zend_Db_Table_Abstract\n{\n    protected $_name = 'orders';\n    // ...\n}\n\n// Model/Repository/UserOrderRepository.php\nclass UserOrderRepository\n{\n    private $userTable;\n    private $orderTable;\n\n    public function __construct(User $userTable, Order $orderTable)\n    {\n        $this->userTable = $userTable;\n        $this->orderTable = $orderTable;\n    }\n\n    public function getOrdersForUser($userId)\n    {\n        $select = $this->orderTable->select();\n        $select->join($this->userTable, 'orders.user_id = users.id');\n        $select->where('users.id = ?', $userId);\n        return $this->orderTable->fetchAll($select);\n    }\n}