use GuzzleHttp\Client;\nuse GuzzleHttp\Handler\MockHandler;\nuse GuzzleHttp\Psr7\Response;\n\n// Create a mock handler\n$mockHandler = new MockHandler([\n    new Response(200, ['Content-Type' => 'application/json'], '{"result": "success"}'),\n]);\n\n// Create a Guzzle client with the mock handler\n$client = new Client(['handler' => $mockHandler]);\n\n// Make a request to your backend service\n$response = $client->get('https://your-backend-service.com/api/search');\n\n// Get the response data\n$result = json_decode($response->getBody()->getContents(), true);\n\n// Print the result\nprint_r($result);