#!/usr/bin/env php\n\n<?php\n\nrequire_once 'path/to/your/sms_gateway_class.php';\n\n$options = getopt('u:p:k:m:');\n\nif (isset($options['u']) && isset($options['p']) && isset($options['k']) && isset($options['m'])) {\n    $username = $options['u'];\n    $password = $options['p'];\n    $apiKey = $options['k'];\n    $message = $options['m'];\n    $phoneNumber = $argv[count($argv) - 1];\n\n    $smsGateway = new SmsGateway($username, $password, $apiKey);\n    $smsGateway->sendSms($phoneNumber, $message);\n} else {\n    echo "Usage: my_custom_send_sms_command -u <username> -p <password> -k <api_key> <phone_number> <message>\n";\n    exit(1);\n}