function parseModuleTags($template) {\n    $pattern = '/<module\s+([^>]+)\s*\/>/';\n    preg_match_all($pattern, $template, $matches);\n\n    foreach ($matches[1] as $match) {\n        $params = array();\n        parse_str(str_replace(array(' ', '"'), array('&', ''), $match), $params);\n\n        $moduleHtml = createModule($params);\n        $template = str_replace('<module ' . $match . '/>', $moduleHtml, $template);\n    }\n\n    return $template;\n}\n\nfunction createModule($params) {\n    // Your module creation function here\n    // This function should return the actual HTML chunk\n    // For example:\n    return '<div>Module ' . $params['display'] . ' with ' . $params['limit'] . ' items</div>';\n}\n\n$template = '<div class="blog">\n<module display="posts" limit="10" show="excerpt" />\n</div>\n<div class="sidebar">\n<module display="users" limit="5" />\n<module display="comment" limit="10" />\n</div>';\n\necho parseModuleTags($template);