<select>\n    <?php\n    for ($hour = 0; $hour < 24; $hour++) {\n        for ($minute = 0; $minute < 60; $minute += 15) {\n            $time = sprintf("%02d:%02d", $hour, $minute);\n            $ampm = ($hour < 12) ? 'AM' : 'PM';\n            if ($hour > 12) $hour -= 12;\n            if ($hour == 0) $hour = 12;\n            echo "<option value='$time'>$hour:$minute $ampm</option>";\n        }\n    }\n    ?>\n</select>