<?php\n// assuming you have a database connection established\n\n// escape user input to prevent SQL injection\n$event = mysqli_real_escape_string($conn, $_REQUEST['event']);\n$from_time = mysqli_real_escape_string($conn, $_REQUEST['from_time']);\n$to_time = mysqli_real_escape_string($conn, $_REQUEST['to_time']);\n$date_event = mysqli_real_escape_string($conn, $_REQUEST['date_event']);\n\n// use prepared statements for better security\n$stmt = $conn->prepare("INSERT INTO calendar (event, from_time, to_time, day) VALUES (?, ?, ?, ?)");\n$stmt->bind_param("ssss", $event, $from_time, $to_time, $date_event);\n$stmt->execute();\n\n// close the statement and connection\n$stmt->close();\n$conn->close();\n?>