// Define a secure upload directory\n$uploadDir = 'uploads/';\n\n// Define allowed file extensions\n$allowedExtensions = ['jpg', 'jpeg', 'png'];\n\n// Get the uploaded file information\n$file = $_FILES['image'];\n\n// Validate file size\nif ($file['size'] > 12485760) {\n    echo 'File too large!';\n    exit;\n}\n\n// Validate file type\nif (!in_array(pathinfo($file['name'], PATHINFO_EXTENSION), $allowedExtensions)) {\n    echo 'Invalid file type!';\n    exit;\n}\n\n// Generate a secure file name\n$fileName = bin2hex(random_bytes(16)) . '.' . pathinfo($file['name'], PATHINFO_EXTENSION);\n\n// Move the uploaded file to the secure directory\n$filePath = $uploadDir . $fileName;\nif (!move_uploaded_file($file['tmp_name'], $filePath)) {\n    echo 'Upload failed!';\n    exit;\n}\n\necho 'File uploaded successfully!';