// Define the current page\n$current_page = basename($_SERVER['PHP_SELF']);\n\n// Your menu items\n$menu_items = array(\n    'A' => 'A.php',\n    'B' => 'B.php',\n    'C' => 'C.php',\n    'D' => 'D.php',\n    'E' => 'E.php'\n);\n\n// Print the menu\nforeach ($menu_items as $label => $page) {\n    $class = ($page == $current_page) ? ' id="black"' : '';\n    echo '<li><a href="' . $page . '"' . $class . '>' . $label . '</a></li>';\n}