$conn = pg_connect("host=$host port=$port dbname=$dbname user=$username password=$password");\n\n// Set spatial query parameters\n$zip_code = '12345';\n$radius = 10; // miles\n\n// Use PostgreSQL's spatial functions to find nearby locations\n$query = "SELECT * FROM locations WHERE ST_DWithin(geometry(longitude, latitude), geometry($zip_code, 0), $radius, 'miles')";\n\n$result = pg_query($conn, $query);\n\nwhile ($row = pg_fetch_assoc($result)) {\n    // Process results...\n}\n\npg_close($conn);