// Configuration\n$db_host = "localhost";\n$db_username = "music123_sri";\n$db_password = "password";\n$db_name = "music123_telugu";\n\n// Connect to database\n$conn = new mysqli($db_host, $db_username, $db_password, $db_name);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Insert data\n$xmlString = "Your XML string here";\n$stmt = $conn->prepare("INSERT INTO xml (File) VALUES (?)");\n$stmt->bind_param("s", $xmlString);\n$stmt->execute();\n\n// Retrieve data\n$stmt = $conn->prepare("SELECT File FROM xml");\n$stmt->execute();\n$result = $stmt->get_result();\nwhile ($row = $result->fetch_assoc()) {\n    echo $row["File"];\n}\n\n// Close connection\n$conn->close();