function findNodesByAttribute(SimpleXMLElement $xml, $attributeName, $attributeValue) {\n    $nodes = [];\n    foreach ($xml->children() as $child) {\n        if (isset($child[$attributeName]) && (string) $child[$attributeName] === $attributeValue) {\n            $nodes[] = $child;\n        }\n        if ($child->count() > 0) {\n            $nodes = array_merge($nodes, findNodesByAttribute($child, $attributeName, $attributeValue));\n        }\n    }\n    return $nodes;\n}\n\n$xml = simplexml_load_string($yourXHTMLString);\n$nodes = findNodesByAttribute($xml, 'class', 'yourClassName');\n\n// Print the results\nforeach ($nodes as $node) {\n    echo $node->getName() . "\n";\n}