// books.php ( receiving end )\n\n$secret_key = 'your_secret_key_here';\n$received_hashed_id = $_GET['id'];\n\n// Get the original book ID from the database or a trusted source\n$book_id = get_book_id_from_database();\n\n$expected_hashed_id = hash_hmac('sha256', $book_id, $secret_key);\n\nif ($received_hashed_id !== $expected_hashed_id) {\n    // Invalid request, handle accordingly\n    exit;\n}\n\n// Valid request, proceed with rendering the book page