function delphiColorToRgb($delphiColor) {\n    if ($delphiColor < 0) {\n        // System colors\n        // You might want to handle these differently or throw an exception\n        // For simplicity, we'll just return black for system colors\n        return array(0, 0, 0);\n    } elseif ($delphiColor <= 16777215) {\n        // RGB color\n        $r = ($delphiColor >> 16) & 255;\n        $g = ($delphiColor >> 8) & 255;\n        $b = $delphiColor & 255;\n        return array($r, $g, $b);\n    } else {\n        // Something went wrong\n        throw new Exception("Invalid Delphi TColor value");\n    }\n}\n\n// Example usage:\n$delphiColor = 8388608;\n$rgb = delphiColorToRgb($delphiColor);\nprint_r($rgb);\n\n$delphiColor = 128;\n$rgb = delphiColorToRgb($delphiColor);\nprint_r($rgb);\n\n$delphiColor = 12632256;\n$rgb = delphiColorToRgb($delphiColor);\nprint_r($rgb);