class DatabaseDriver {\n    private $nodes = array(\n        'master' => 'mysql://user:password@master-host:3306/database',\n        'slave1' => 'mysql://user:password@slave1-host:3306/database',\n        'slave2' => 'mysql://user:password@slave2-host:3306/database',\n    );\n\n    private $currentNode = 0;\n\n    public function query($query) {\n        if (is_write_operation($query)) {\n            // Direct writes to master node\n            $node = $this->nodes['master'];\n        } else {\n            // Use load balancing algorithm to select a slave node\n            $node = $this->getSlaveNode();\n        }\n\n        // Execute query on selected node\n        $connection = new mysqli($node);\n        $result = $connection->query($query);\n        $connection->close();\n\n        return $result;\n    }\n\n    private function getSlaveNode() {\n        // Simple round-robin load balancing algorithm\n        $this->currentNode = ($this->currentNode + 1) % count($this->nodes);\n        return $this->nodes['slave' . $this->currentNode];\n    }\n\n    private function is_write_operation($query) {\n        // Simple check to determine if query is a write operation\n        return preg_match('/^(INSERT|UPDATE|DELETE)/i', $query);\n    }\n}