function proxy($url) {\n    $ch = curl_init($url);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    $response = curl_exec($ch);\n    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);\n    curl_close($ch);\n\n    // Modify the response data as needed\n    $modifiedResponse = str_replace('original string', 'modified string', $response);\n\n    // Set proper headers\n    header('Content-Type: ' . getMimeType($url));\n    header('Content-Disposition: attachment; filename="' . basename($url) . '"');\n    header('Content-Length: ' . strlen($modifiedResponse));\n\n    // Output the modified response\n    echo $modifiedResponse;\n}\n\nfunction getMimeType($url) {\n    $mimeTypes = array(\n        'pdf' => 'application/pdf',\n        'txt' => 'text/plain',\n        // Add more mime types as needed\n    );\n\n    $extension = pathinfo($url, PATHINFO_EXTENSION);\n    return $mimeTypes[$extension] ?? 'application/octet-stream';\n}