function mix_strings($str1, $str2) {\n    $mixed = '';\n    $len1 = strlen($str1);\n    $len2 = strlen($str2);\n    $max_len = max($len1, $len2);\n\n    for ($i = 0; $i < $max_len; $i++) {\n        if ($i < $len1) {\n            $mixed .= $str1[$i];\n        }\n        if ($i < $len2) {\n            $mixed .= $str2[$i];\n        }\n    }\n\n    return $mixed;\n}\n\n$string1 = '9cb5jplgvsiedji9mi9o6a8qq1';\n$string2 = '5d41402abc4b2a76b9719d911017c592';\n\n$mixed_string = mix_strings($string1, $string2);\necho $mixed_string;