require_once "../../includes/constants.php";\n\n$dbh = new mysqli(DB_SERVER, DB_USER, DB_PASSWORD, DB_NAME);\n\nif ($dbh->connect_error) {\n    die("Connection failed: " . $dbh->connect_error);\n}\n\n$action = $_POST['action'];\n$name = $_POST['name'];\n$id = $_POST['id'];\n\nif ($action == "new") {\n    $stmt = $dbh->prepare("INSERT INTO place (name) VALUES (?)");\n    $stmt->bind_param("s", $name);\n    $stmt->execute();\n} elseif ($action == "edit") {\n    $stmt = $dbh->prepare("UPDATE place SET name = ? WHERE id = ?");\n    $stmt->bind_param("si", $name, $id);\n    $stmt->execute();\n} elseif ($action == "delete") {\n    $stmt = $dbh->prepare("DELETE FROM place WHERE id = ?");\n    $stmt->bind_param("i", $id);\n    $stmt->execute();\n}\n\necho "<table><tbody>";\n$stmt = $dbh->prepare("SELECT * FROM place");\n$stmt->execute();\n$result = $stmt->get_result();\nwhile ($row = $result->fetch_assoc()) {\n    echo "<tr><td id=" . $row["id"] . " class=inputfield_td><input class=inputfield_place type=text value=" . $row["name"] . " /></td><td class=place_name>" . $row["name"] . "</td><td class=edit>edit</td><td class=cancel>cancel</td><td class=delete>delete</td><td class=save>SAVE</td></tr> \n";\n}\necho "</tbody>";\necho "</table>";\necho "<input type=text class=inputfield_visible />";\necho "<button class=new>Neu</button>";\n\n$dbh->close();