<?php\n$ventrilo_server_ip = 'your_ventrilo_server_ip';\n$ventrilo_server_port = 3784; // default port\n$api_key = 'your_api_key';\n\n// Create a socket to connect to the Ventrilo Server API\n$sock = fsockopen($ventrilo_server_ip, $ventrilo_server_port);\n\n// Send the API request to retrieve server status\n$request = "GET /status?api_key=$api_key HTTP/1.1\r\n";\nfputs($sock, $request);\n\n// Read the response from the API\n$response = '';\nwhile (!feof($sock)) {\n    $response .= fgets($sock, 128);\n}\nfclose($sock);\n\n// Parse the response (XML format)\n$xml = new SimpleXMLElement($response);\n\n// Extract the user and channel information\n$users = array();\nforeach ($xml->xpath('//user') as $user) {\n    $users[] = array(\n        'name' => (string)$user->name,\n        'channel' => (string)$user->channel\n    );\n}\n\n// Display the user and channel information\nforeach ($users as $user) {\n    echo "$user[name] is connected to $user[channel]\n";\n}