function procLogin($username, $password) {\n    $mysqli = new mysqli("your_host", "your_username", "your_password", "your_database");\n\n    $query = "SELECT * FROM members WHERE login = ?";\n\n    $stmt = $mysqli->prepare($query);\n    $stmt->bind_param("s", $username);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    if ($result->num_rows > 0) {\n        $row = $result->fetch_assoc();\n        // Check password using password_verify()\n        if (password_verify($password, $row['passwd'])) {\n            return array($row['member_id'], $row['userlevel']);\n        } else {\n            return 'Invalid password';\n        }\n    } else {\n        return 'Invalid username';\n    }\n}