function obfuscate_id($id, $key) {\n    return $id ^ $key;\n}\n\nfunction deobfuscate_id($obfuscated_id, $key) {\n    return $obfuscated_id ^ $key;\n}\n\n// Example usage:\n$id = 1234;\n$key = 0xCAFEBABE; // Choose a secret key\n\n$obfuscated_id = obfuscate_id($id, $key);\necho "Obfuscated ID: $obfuscated_id\n";\n\n$deobfuscated_id = deobfuscate_id($obfuscated_id, $key);\necho "Deobfuscated ID: $deobfuscated_id\n";