$input_array = array(\n    'type'  => 'text',\n    'class' => 'input',\n    'name'  => 'username',\n    'id'    => 'username',\n    'value' => '',\n    'size'  => '30',\n    'rows'  => '',\n    'cols'  => ''\n);\n\n$input = '<input ';\nforeach ($input_array as $key => $value) {\n    if (!empty($value)) { // ignore empty values\n        $input .= $key . '="' . $value . '" ';\n    }\n}\n$input .= '/>';\n\necho $input;