$stmt = $pdo->prepare('\n    SELECT a.aid, a.name, s.sid, s.length\n    FROM albums a\n    JOIN songs s ON a.aid = s.aid\n    WHERE a.aid IN (:aids)\n');\n\n$aids = array(1, 2, 3);\n$stmt->bindParam(':aids', implode(',', $aids));\n$stmt->execute();\n\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// loop through the results and do something with the data\nforeach ($results as $row) {\n    echo $row['name'] . ' - ' . $row['sid'] . ' - ' . $row['length'] . PHP_EOL;\n}