// module/Application/src/Route/DatabaseRouter.php\n\nnamespace Application\Route;\n\nuse Zend\Router\RouteInterface;\nuse Zend\Router\Exception;\nuse Zend\Db\Adapter\AdapterInterface;\nuse Zend\Db\TableGateway\TableGateway;\n\nclass DatabaseRouter implements RouteInterface\n{\n    private $adapter;\n    private $tableGateway;\n\n    public function __construct(AdapterInterface $adapter)\n    {\n        $this->adapter = $adapter;\n        $this->tableGateway = new TableGateway('sitePage', $this->adapter);\n    }\n\n    public function match($request)\n    {\n        $pageName = $request->getUri()->getPath();\n\n        $row = $this->tableGateway->select(['pageName' => $pageName])->current();\n\n        if (!$row) {\n            throw new Exception\RuntimeException('Page not found');\n        }\n\n        $controller = $row->controller;\n        $module = $row->module;\n        $view = $row->view;\n\n        return [\n            'controller' => $controller,\n            'module' => $module,\n            'view' => $view,\n        ];\n    }\n\n    public function assemble(array $params = [], array $options = [])\n    {\n        // Assemble URL from params (not implemented in this example)\n    }\n}