use PhpAmqpLib\Connection\AMQPStreamConnection;\nuse Zend_Mail_Transport_Smtp;\nuse Zend_Mail;\n\n// Create a connection to RabbitMQ\n$connection = new AMQPStreamConnection('localhost', 5672, 'guest', 'guest');\n$channel = $connection->channel();\n\n// Consume messages from the email queue\n$channel->basic_consume('email_queue', '', false, true, false, false, function (AMQPMessage $message) {\n    // Send the email using Zend_Mail\n    $transport = new Zend_Mail_Transport_Smtp('localhost');\n    $mail = new Zend_Mail();\n    $mail->setBodyText($message->getBody()['emailBody']);\n    $mail->setFrom('your_from_email@example.com');\n    $mail->addTo($message->getBody()['recipient']);\n    $transport->send($mail);\n});\n\n// Close the connection\n$channel->close();\n$connection->close();