class MyClass {\n    private $person = array();\n\n    public function __set($name, $value) {\n        if ($name == 'person') {\n            $this->person = $value;\n        } elseif (isset($this->person[$name])) {\n            $this->person[$name] = $value;\n        }\n    }\n\n    public function __get($name) {\n        if ($name == 'person') {\n            return $this->person;\n        } elseif (isset($this->person[$name])) {\n            return $this->person[$name];\n        }\n    }\n}\n\n$obj = new MyClass();\n$obj->person['name'] = 'perro';