<?php\n\nfunction loop_array($array, $callback) {\n    // Loop through the array and apply the callback function\n    foreach ($array as $value) {\n        $callback($value);\n    }\n}\n\nfunction loop_array_recursive($array, $callback) {\n    // Loop through the array recursively and apply the callback function\n    foreach ($array as $value) {\n        if (is_array($value)) {\n            loop_array_recursive($value, $callback);\n        } else {\n            $callback($value);\n        }\n    }\n}\n\n// Add more loop functions as needed...