function extracting_data($table, $fields, $condition, $order, $limit) {\n    $query = "SELECT $fields FROM $table WHERE id = ? ORDER BY $order LIMIT $limit";\n    $stmt = $this->conn->prepare($query);\n    $stmt->bind_param("i", $condition);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    $data = array();\n    while ($row = $result->fetch_assoc()) {\n        $data[] = $row;\n    }\n    $stmt->close();\n    return $data;\n}