// Assuming $database is your PDO connection\n\n// Prepare the SQL query with a placeholder for the parameter\n$statement = $database->prepare("SELECT * FROM widgets WHERE something = :something");\n\n// Bind the parameter to the placeholder, ensuring it's treated as a string\n$somethingElse = 'something else'; // This can be any dynamic value\n$statement->bindParam(':something', $somethingElse);\n\n// Execute the prepared statement\n$statement->execute();\n\n// Loop through the results\nforeach ($statement as $results) {\n    echo $results["widget_name"];\n}\n\n// Alternatively, you can fetch all results at once and loop through them\n$results = $statement->fetchAll(PDO::FETCH_ASSOC);\nforeach ($results as $result) {\n    echo $result["widget_name"];\n}