$first = 2;\n$second = 5;\n$operation = '*';\n\nswitch ($operation) {\n    case '+':\n        $answer = $first + $second;\n        break;\n    case '-':\n        $answer = $first - $second;\n        break;\n    case '*':\n        $answer = $first * $second;\n        break;\n    case '/':\n        if ($second != 0) {\n            $answer = $first / $second;\n        } else {\n            // handle division by zero error\n            $answer = null;\n        }\n        break;\n    default:\n        // handle invalid operation\n        $answer = null;\n        break;\n}\n\necho $answer; // outputs 10