require_once 'Zend/Soap/Client.php';\n\n$client = new Zend_Soap_Client('http://example.com/soap/wsdl');\n$client->setOptions(array('soap_version' => SOAP_1_2));\n\n// Assuming the SOAP method 'uploadFile' requires a multi-part message\n$multipart = new SoapVar(\n    array(\n        'filename' => 'example.txt',\n        'filedata' => file_get_contents('example.txt')\n    ),\n    SOAP_ENC_OBJECT,\n    'multipart'\n);\n\n$result = $client->uploadFile($multipart);