use PhpImap\PhpImap;\n\n$mailbox = new PhpImap\PhpImap();\n$mailbox->connect('pop.example.com', 110, '/ssl');\n$mailbox->login('your_email', 'your_password');\n\n// Fetch all emails\n$emails = $mailbox->searchMailbox('ALL');\n\n// Loop through each email\nforeach ($emails as $email) {\n    // Get the email body\n    $body = $mailbox->getBody($email['uid']);\n\n    // Do something with the email body...\n\n    // Delete the email\n    $mailbox->deleteMail($email['uid']);\n}\n\n$mailbox->disconnect();