function get_distinct_size_for_bracelets($myvalue) {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n    $stmt = $pdo->prepare("SELECT DISTINCT size FROM mytable WHERE id = 27 AND type = :myvalue ORDER BY size");\n    $stmt->bindParam(':myvalue', $myvalue);\n    $stmt->execute();\n    $result = $stmt->fetchAll();\n    return $result;\n}