$rtfFile = 'path/to/your/file.rtf';\n$imageFile = 'path/to/your/image.jpg';\n\n$rtfContent = file_get_contents($rtfFile);\n$placeholderPos = strpos($rtfContent, '{photo}');\n\nif ($placeholderPos !== false) {\n    $imageData = file_get_contents($imageFile);\n    $imageRtf = '{\pict\jpegblip\picw120\pich120\picwgoal120\pichgoal120' . "\n";\n    $imageRtf .= $imageData . "\n";\n    $imageRtf .= '}';\n\n    $rtfContent = substr_replace($rtfContent, $imageRtf, $placeholderPos, strlen('{photo}'));\n}\n\nfile_put_contents($rtfFile, $rtfContent);