function update($table, $fields, $values, $conditions, $conn) {\n    $sql = "UPDATE $table SET " . implode(", ", array_map(function ($field, $value) {\n        return "$field = ?";\n    }, $fields, $values));\n    if ($conditions) {\n        $sql .= " WHERE " . implode(" AND ", $conditions);\n    }\n    $stmt = $conn->prepare($sql);\n    $stmt->bind_param(str_repeat('s', count($values)), ...$values);\n    $stmt->execute();\n    return $stmt->affected_rows;\n}