$dbAdapter = new Zend_Db_Adapter_Mysqli($config);\n$dynamicSheet = new DynamicSheet($dbAdapter);\n\n// Create a new sheet\n$sheetId = $dynamicSheet->createSheet('price_sheet');\n\n// Add rows\n$rowId1 = $dynamicSheet->addRow($sheetId, 'model A');\n$rowId2 = $dynamicSheet->addRow($sheetId, 'model B');\n\n// Add cells\n$dynamicSheet->addCell($sheetId, $rowId1, '3', '9$');\n$dynamicSheet->addCell($sheetId, $rowId2, '3', '10$');\n\n// Retrieve a cell value\n$cellValue = $dynamicSheet->getCell($sheetId, 'model B', '3');\necho $cellValue; // Output: 10$