class DynamicSheet {\n    private $dbAdapter;\n\n    public function __construct($dbAdapter) {\n        $this->dbAdapter = $dbAdapter;\n    }\n\n    public function createSheet($name, $description = null) {\n        // Insert into sheets table\n        $this->dbAdapter->insert('sheets', array('name' => $name, 'description' => $description));\n        return $this->dbAdapter->lastInsertId();\n    }\n\n    public function addRow($sheetId, $rowName) {\n        // Insert into sheet_rows table\n        $this->dbAdapter->insert('sheet_rows', array('sheet_id' => $sheetId, 'row_name' => $rowName));\n        return $this->dbAdapter->lastInsertId();\n    }\n\n    public function addCell($sheetId, $rowId, $columnName, $cellValue) {\n        // Insert into sheet_cells table\n        $this->dbAdapter->insert('sheet_cells', array(\n            'sheet_id' => $sheetId,\n            'row_id' => $rowId,\n            'column_name' => $columnName,\n            'cell_value' => $cellValue\n        ));\n    }\n\n    public function getCell($sheetId, $rowName, $columnName) {\n        // Query sheet_cells table\n        $query = $this->dbAdapter->select()\n            ->from('sheet_cells')\n            ->where('sheet_id = ?', $sheetId)\n            ->where('row_id = ?', $this->getRowId($sheetId, $rowName))\n            ->where('column_name = ?', $columnName);\n        return $this->dbAdapter->fetchOne($query);\n    }\n\n    private function getRowId($sheetId, $rowName) {\n        // Query sheet_rows table\n        $query = $this->dbAdapter->select()\n            ->from('sheet_rows')\n            ->where('sheet_id = ?', $sheetId)\n            ->where('row_name = ?', $rowName);\n        return $this->dbAdapter->fetchOne($query);\n    }\n}