try {\n    $DB = new PDO("mysql:host=localhost;dbname=dbname", "user", "pass");\n    $stmt = $DB->prepare("SELECT * FROM report_clientinfo");\n    $stmt->execute();\n    $resultset = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    if (empty($resultset)) {\n        exit("ERROR: getClientInfo query failed.");\n    }\n    print_r($resultset);\n} catch (PDOException $e) {\n    echo $e->getMessage() . "\n";\n} finally {\n    $DB = null;\n}