// Generate token and store session variable\n$token = bin2hex(random_bytes(16));\n$sessionVariable = $_SESSION['your_variable'];\n$storage[$token] = $sessionVariable;\n\n// Pass token with redirect\necho '<button onclick="javascript:window.location.href=\'http://www.another.page.com/?token=' . $token . '\'; return false;">';\n\n// On the redirected page\nif (isset($_GET['token'])) {\n    $token = $_GET['token'];\n    if (isset($storage[$token])) {\n        $sessionVariable = $storage[$token];\n        // Use the session variable\n    } else {\n        // Handle invalid token\n    }\n}