// restful.php\n\n// Define a class to handle RESTful requests\nclass RestfulService {\n    private $data;\n\n    // Constructor to initialize data\n    public function __construct() {\n        $this->data = array(\n            "1" => array("name" => "John", "age" => 30),\n            "2" => array("name" => "Jane", "age" => 25)\n        );\n    }\n\n    // Method to handle GET requests\n    public function get($id) {\n        if (isset($this->data[$id])) {\n            return json_encode($this->data[$id]);\n        } else {\n            return json_encode(array("error" => "Not found"));\n        }\n    }\n\n    // Method to handle POST requests\n    public function post($data) {\n        $this->data[] = $data;\n        return json_encode(array("message" => "Data added successfully"));\n    }\n}\n\n// Create an instance of the RestfulService class\n$service = new RestfulService();\n\n// Define a router to handle HTTP requests\n$uri = parse_url($_SERVER['REQUEST_URI'], PHP_URL_PATH);\n$method = $_SERVER['REQUEST_METHOD'];\n\n// Handle GET requests\nif ($method == 'GET') {\n    $id = substr($uri, 1); // Remove leading slash\n    echo $service->get($id);\n}\n\n// Handle POST requests\nif ($method == 'POST') {\n    $data = json_decode(file_get_contents('php://input'), true);\n    echo $service->post($data);\n}