<table>\n    <tr>\n        <th>Column</th>\n        <th>Condition</th>\n        <th>Value</th>\n        <th>Logical Operator</th>\n    </tr>\n    <?php foreach ($columns as $column => $data_type): ?>\n    <tr>\n        <td>\n            <select name="column[]">\n                <option value="<?php echo $column; ?>"><?php echo $column; ?></option>\n            </select>\n        </td>\n        <td>\n            <select name="condition[]">\n                <?php foreach ($conditions as $condition => $label): ?>\n                <option value="<?php echo $condition; ?>"><?php echo $label; ?></option>\n                <?php endforeach; ?>\n            </select>\n        </td>\n        <td>\n            <input type="text" name="value[]" />\n        </td>\n        <td>\n            <select name="logical_operator[]">\n                <?php foreach ($logical_operators as $operator => $label): ?>\n                <option value="<?php echo $operator; ?>"><?php echo $label; ?></option>\n                <?php endforeach; ?>\n            </select>\n        </td>\n    </tr>\n    <?php endforeach; ?>\n</table>