// application/hooks/Audit.php\n\nclass Audit {\n    public function after_update($table, $row_id, $data_before, $data_after) {\n        // Insert audit data into the audit table\n        $this->db->insert('audit', array(\n            'table_name' => $table,\n            'row_id' => $row_id,\n            'data_before' => serialize($data_before),\n            'data_after' => serialize($data_after),\n            'action' => 'UPDATE',\n            'timestamp' => now(),\n            'user_id' => $this->session->userdata('user_id')\n        ));\n    }\n\n    public function after_insert($table, $row_id, $data) {\n        // Insert audit data into the audit table\n        $this->db->insert('audit', array(\n            'table_name' => $table,\n            'row_id' => $row_id,\n            'data_before' => null,\n            'data_after' => serialize($data),\n            'action' => 'INSERT',\n            'timestamp' => now(),\n            'user_id' => $this->session->userdata('user_id')\n        ));\n    }\n}