$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n$keywords = explode(' ', $title);\n$sql = "SELECT * FROM tbl_content WHERE status='t' AND (";\nforeach ($keywords as $i => $keyword) {\n    $sql .= "article_name LIKE :keyword_$i OR ";\n}\n$sql = substr($sql, 0, -4); // remove the last " OR "\n$sql .= ") ORDER BY id DESC LIMIT 5";\n$stmt = $pdo->prepare($sql);\nforeach ($keywords as $i => $keyword) {\n    $stmt->bindValue(":keyword_$i", "%$keyword%");\n}\n$stmt->execute();\n$relatedArticles = $stmt->fetchAll();