require_once('tcpdf/tcpdf.php');\n\n// Set up the PDF document\n$pdf = new TCPDF(PDF_PAGE_ORIENTATION, PDF_UNIT, PDF_PAGE_FORMAT, true, 'UTF-8', false);\n\n// Set the template (you can use an HTML file or a string)\n$template = '<html><body>'.\n            '<h1>Invoice #123</h1>'.\n            '<table>'.\n            '<tr><td>Product</td><td>Quantity</td><td>Price</td></tr>'.\n            '<tr><td>Product A</td><td>2</td><td>10.99</td></tr>'.\n            '</table>'.\n            '</body></html>';\n\n// Replace placeholders with actual data (e.g., from a database)\n$template = str_replace('{{invoice_number}}', '123', $template);\n\n// Generate the PDF\n$pdf->AddPage();\n$pdf->writeHTML($template, true, false, true, false, '');\n\n// Output the PDF\n$pdf->Output('invoice.pdf', 'I');