// src/Traits/DataSaverTrait.php\ntrait DataSaverTrait\n{\n    public function saveData(array $data)\n    {\n        // Get the model instance using the TableRegistry\n        $model = TableRegistry::getTableLocator()->get($this->getModelName());\n\n        // Save the data using the model\n        $model->saveMany($data);\n    }\n\n    abstract protected function getModelName(): string;\n}