$timezoneList = array();\nforeach (DateTimeZone::listIdentifiers() as $timezone) {\n    $zone = new DateTimeZone($timezone);\n    $offset = $zone->getOffset(new DateTime());\n    $region = explode('/', $timezone)[0];\n    $timezoneList[$region][$timezone] = $offset;\n}\n\n// Render the list\nforeach ($timezoneList as $region => $timezones) {\n    echo "<h2>$region</h2>";\n    foreach ($timezones as $timezone => $offset) {\n        echo "<option value='$timezone'>$timezone (UTC" . formatOffset($offset) . ")</option>";\n    }\n}\n\n// Helper function to format offset\nfunction formatOffset($offset) {\n    $hours = floor($offset / 3600);\n    $minutes = floor(($offset % 3600) / 60);\n    return sprintf("%+03d:%02d", $hours, $minutes);\n}