$tweets = $your_twitter_api_response['results'];\n\n// Filter tweets by from_user\n$filtered_tweets = array_filter($tweets, function($tweet) use ($whoever) {\n    return $tweet['from_user'] == $whoever;\n});\n\n// Extract text field and remove #runlogger\n$texts = array_map(function($tweet) {\n    return str_replace('#runlogger', '', $tweet['text']);\n}, $filtered_tweets);\n\n// Count occurrences of each text\n$text_counts = array_count_values($texts);\n\nprint_r($text_counts);