$tables = mssql_query("SELECT name FROM sys.tables WHERE type = 'U'");\nwhile ($table = mssql_fetch_assoc($tables)) {\n    $table_name = $table['name'];\n    $query = "SELECT * FROM $table_name";\n    $results = mssql_query($query);\n    $data = array();\n    while ($row = mssql_fetch_assoc($results)) {\n        $data[] = $row;\n    }\n    file_put_contents("$table_name.json", json_encode($data));\n}