<?php\n$location = "http://localhost/folwer.bmp";\n\n// Get the image dimensions\nlist($width, $height) = getimagesize($location);\n\n// Set the desired dimensions\n$desired_width = 120;\n$desired_height = 240;\n\n// Calculate the aspect ratio\n$aspect_ratio = $width / $height;\n\n// Create a new image with the desired dimensions\n$image = imagecreatetruecolor($desired_width, $desired_height);\n\n// Copy the original image onto the new image, resizing it\nimagecopyresampled($image, imagecreatefrombmp($location), 0, 0, 0, 0, $desired_width, $desired_height, $width, $height);\n\n// Output the resized image\nheader("Content-Type: image/bmp");\nimagebmp($image);\n\n// Clean up\nimagedestroy($image);\n?>